import React, { useContext } from "react";
import { DarkModeContext } from "./DarkModeContext";
import { FaGithub, FaExternalLinkAlt, FaCode } from "react-icons/fa";

const Projects = () => {
  const { isDarkMode } = useContext(DarkModeContext);

  const projects = [
    {
      title: "SipSense: Caffeine tracking in Coffee using React",
      description: (
        <>
          <p>
            - Launched a data-driven platform for caffeine monitoring via React, which aggregates user data to provide health
            insights; achieved a response time of under 2 seconds for user queries, enhancing overall user experience.
          </p>
          <p>
            - Implemented JWT authentication for secure sign-in and sign-out,
            ensuring a personalized and protected user experience.
          </p>
        </>
      ),
      githubLink: "https://github.com/Rohann-79/Coffee-Tracker",
      liveDemo: "https://coffee-tracker-zeta.vercel.app/",
    },
    {
      title: "Nexus AI: Real-time Age and Gender Detection",
      description: (
        <>
          <p>
            - Developed a real-time age and gender detection system using OpenCV and deep learning models,
            achieving 95% accuracy in age group classification.
          </p>
          <p>
            - Implemented face detection and feature extraction algorithms,
            processing 30+ frames per second on standard hardware.
          </p>
        </>
      ),
      githubLink: "https://github.com/Rohann-79/Age-Gender-Detection",
      liveDemo: null,
    },
    {
      title: "Finance Tracker: Personal Finance Management",
      description: (
        <>
          <p>
            - Built a comprehensive personal finance management system with expense tracking,
            budget planning, and financial analytics.
          </p>
          <p>
            - Implemented data visualization using Chart.js for intuitive financial insights
            and added export functionality for financial reports.
          </p>
        </>
      ),
      githubLink: "https://github.com/Rohann-79/Finance-Tracker",
      liveDemo: "https://finance-tracker-demo.vercel.app/",
    },
  ];

  return (
    <section
      id="projects"
      className={`py-20 ${
        isDarkMode
          ? "bg-gradient-to-br from-[#0a0a1a] via-[#1a1a2e] to-[#0a0a1a]"
          : "bg-gradient-to-r from-blue-50 to-purple-50"
      }`}
    >
      <div className="container mx-auto px-4">
        <h2
          className={`text-4xl font-bold text-center mb-4 ${
            isDarkMode ? "text-white" : "text-gray-800"
          } code-text`}
          data-aos="fade-right"
        >
          Projects
        </h2>
        <p
          className={`text-center mb-12 ${
            isDarkMode ? "text-gray-400" : "text-gray-600"
          }`}
          data-aos="fade-up"
        >
          Some things I've built
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project, index) => (
            <div
              key={index}
              className={`p-6 rounded-lg ${
                isDarkMode ? "bg-[#1a1a2e]/50" : "bg-white/50"
              } backdrop-blur-sm hover:scale-105 transition-transform duration-300`}
              data-aos="fade-up"
              data-aos-delay={index * 200}
            >
              <div className="flex items-center space-x-2 mb-4">
                <FaCode className={`text-xl ${
                  isDarkMode ? "text-purple-400" : "text-blue-600"
                }`} />
                <h3 className={`text-xl font-semibold ${
                  isDarkMode ? "text-white" : "text-gray-800"
                }`}>
                  {project.title}
                </h3>
              </div>

              <div className={`space-y-2 mb-6 ${
                isDarkMode ? "text-gray-300" : "text-gray-700"
              }`}>
                {project.description}
              </div>

              <div className="flex space-x-4">
                <a
                  href={project.githubLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={`flex items-center space-x-2 px-4 py-2 rounded-lg ${
                    isDarkMode
                      ? "bg-[#2a2a3e] text-white hover:bg-[#3a3a4e]"
                      : "bg-gray-100 text-gray-800 hover:bg-gray-200"
                  } transition-colors duration-300`}
                >
                  <FaGithub />
                  <span>Code</span>
                </a>
                {project.liveDemo && (
                  <a
                    href={project.liveDemo}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`flex items-center space-x-2 px-4 py-2 rounded-lg ${
                      isDarkMode
                        ? "bg-[#2a2a3e] text-white hover:bg-[#3a3a4e]"
                        : "bg-gray-100 text-gray-800 hover:bg-gray-200"
                    } transition-colors duration-300`}
                  >
                    <FaExternalLinkAlt />
                    <span>Live Demo</span>
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
