import React, { useContext } from "react";
import { DarkModeContext } from "./DarkModeContext";
import { FaBriefcase } from "react-icons/fa";

const Experience = () => {
  const { isDarkMode } = useContext(DarkModeContext);

  const experiences = [
    {
      company: "Ezynest LLC",
      role: "Backend Developer Intern",
      duration: "Jun 2023 - Present",
      description: [
        "Engineered and maintained scalable RESTful APIs using Spring Boot, improving frontend functionality and optimizing user experience through efficient data handling, reducing response time by 30%.",
        "Designed and optimized relational database schemas in PostgreSQL, ensuring data integrity, enhancing query performance by 40%, and enabling efficient data retrieval for high-traffic applications.",
      ],
      tech: ["Spring Boot", "PostgreSQL", "REST APIs", "Java"],
    },
    {
      company: "Freelance",
      role: "Full-Stack Developer",
      duration: "Jan 2022 - May 2022",
      description: [
        "Developed and deployed web applications using React and Node.js.",
        "Implemented user authentication and authorization systems.",
        "Worked closely with clients to deliver customized solutions.",
      ],
      tech: ["React", "Node.js", "MongoDB", "JWT"],
    },
  ];

  return (
    <section
      id="experience"
      className={`py-20 ${
        isDarkMode
          ? "bg-gradient-to-br from-[#0a0a1a] via-[#1a1a2e] to-[#0a0a1a]"
          : "bg-gradient-to-r from-blue-50 to-purple-50"
      }`}
    >
      <div className="container mx-auto px-4">
        <h2
          className={`text-4xl font-bold text-center mb-4 ${
            isDarkMode ? "text-white" : "text-gray-800"
          } code-text`}
          data-aos="fade-right"
        >
          Experience
        </h2>
        <p
          className={`text-center mb-12 ${
            isDarkMode ? "text-gray-400" : "text-gray-600"
          }`}
          data-aos="fade-up"
        >
          My professional journey
        </p>

        <div className="space-y-8">
          {experiences.map((exp, index) => (
            <div
              key={index}
              className={`p-6 rounded-lg hover-card ${
                isDarkMode ? "bg-[#1a1a2e]/50" : "bg-white"
              } backdrop-blur-sm`}
              data-aos="fade-up"
              data-aos-delay={index * 200}
            >
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-4">
                  <div className={`p-3 rounded-lg ${
                    isDarkMode ? "bg-[#2a2a3e]" : "bg-blue-100"
                  }`}>
                    <FaBriefcase className={`text-2xl ${
                      isDarkMode ? "text-purple-400" : "text-blue-600"
                    }`} />
                  </div>
                  <div>
                    <h3
                      className={`text-xl font-semibold ${
                        isDarkMode ? "text-white" : "text-gray-800"
                      }`}
                    >
                      {exp.role}
                    </h3>
                    <p className={`${
                      isDarkMode ? "text-purple-400" : "text-blue-600"
                    }`}>
                      {exp.company}
                    </p>
                  </div>
                </div>
                <span className={`text-sm ${
                  isDarkMode ? "text-gray-400" : "text-gray-600"
                }`}>
                  {exp.duration}
                </span>
              </div>

              <ul className={`space-y-2 mb-4 ${
                isDarkMode ? "text-gray-300" : "text-gray-700"
              }`}>
                {exp.description.map((point, i) => (
                  <li key={i} className="flex items-start space-x-2">
                    <span className={`mt-1 ${
                      isDarkMode ? "text-purple-400" : "text-blue-600"
                    }`}>â€¢</span>
                    <span>{point}</span>
                  </li>
                ))}
              </ul>

              <div className="flex flex-wrap gap-2">
                {exp.tech.map((tech, i) => (
                  <span
                    key={i}
                    className={`px-3 py-1 rounded-full text-sm ${
                      isDarkMode
                        ? "bg-[#2a2a3e] text-purple-400"
                        : "bg-blue-100 text-blue-600"
                    }`}
                  >
                    {tech}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Experience;
